import React, { useState, useEffect } from 'react';
import { useData } from '../context/DataContext'; // Import custom hook to access context data

const SubscribersList = () => {
    const { subscribers, setSubscribers } = useData(); // Get companies, shipments, and subscribers from context
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleRemoveSubscriber = async (email) => {
    try {
      const response = await fetch('http://localhost:4000/api/subscribers/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        setMessage('Subscriber removed successfully');
        setMessageType('success');
        setSubscribers(subscribers.filter((subscriber) => subscriber.email !== email)); // Remove subscriber from the list
      } else {
        setMessage('Failed to remove subscriber');
        setMessageType('error');
      }
    } catch (error) {
      setMessage('An error occurred while removing the subscriber');
      setMessageType('error');
    }
  };

  return (
    <div className="flex bg-gray-100">
        <div className="flex-1 p-8 ml-64">
      {subscribers.length === 0 ? (
        <p>No subscribers found.</p>
      ) : (
        subscribers
          .filter((subscriber) => subscriber.status === 'subscribed')
          .map((subscriber, i) => (
            <div key={i} className="bg-white p-4 rounded-lg shadow-md flex items-center justify-between">
              <p>{subscriber.email_address}</p>
              <button
                onClick={() => handleRemoveSubscriber(subscriber.email)}
                className="bg-red-500 text-white px-4 py-2 rounded"
              >
                Remove
              </button>
            </div>
          ))
      )}
    </div>
    </div>
  );
};

export default SubscribersList;
