// Sidebar.js
import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Sidebar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  return (
    <div className="w-64 bg-gray-800 text-white p-6 h-full fixed top-0 left-0">
      <h2 className="text-xl font-bold mb-6">
        <Link to="/">Admin Dashboard</Link>
      </h2>
      <nav>
        <ul>
          <li className="mb-4">
            <Link to="/companies" className="text-lg hover:text-gray-300">View Companies</Link>
          </li>
          <li className="mb-4">
            <Link to="/subscribers" className="text-lg hover:text-gray-300">View Subscribers</Link>
          </li>
          <li className="mt-6">
            <button
              onClick={handleLogout}
              className="bg-red-500 text-white py-2 px-4 rounded-lg w-full text-center hover:bg-red-600"
            >
              Logout
            </button>
          </li>
        </ul>
      </nav>
    </div>
  );
};

export default Sidebar;
