import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';

const AdminDashboard = () => {
  const [subscribers, setSubscribers] = useState([]);
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState(''); // 'success' or 'error'
  
  // Dummy data for stats
  const dummyStats = {
    companies: 25,
    shipments: 42,
    pendingSubscribers: 5,
  };

  useEffect(() => {
    // Fetch the list of subscribers when the component mounts
    const fetchSubscribers = async () => {
      try {
        const response = await fetch('http://localhost:4000/api/subscribers');
        if (response.ok) {
          const data = await response.json();
          setSubscribers(data); // Store the subscribers data in the state
        } else {
          setMessage('Failed to load subscribers');
          setMessageType('error');
        }
      } catch (error) {
        setMessage('An error occurred while fetching subscribers');
        setMessageType('error');
      }
    };

    fetchSubscribers();
  }, []);

  const handleRemoveSubscriber = async (email) => {
    // Call the backend to remove the subscriber
    try {
      const response = await fetch('http://localhost:4000/api/subscribers/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        setMessage('Subscriber removed successfully');
        setMessageType('success');
        setSubscribers(subscribers.filter((subscriber) => subscriber.email !== email)); // Update the UI by removing the subscriber
      } else {
        setMessage('Failed to remove subscriber');
        setMessageType('error');
      }
    } catch (error) {
      setMessage('An error occurred while removing the subscriber');
      setMessageType('error');
    }
  };

  return (
    <div className="flex h-screen bg-gray-100">
      {/* Sidebar */}
      <div className="w-64 bg-gray-800 text-white p-6">
        <h2 className="text-xl font-bold mb-6">Admin Dashboard</h2>
        <nav>
          <ul>
            <li className="mb-4">
              <Link to="/" className="text-lg hover:text-gray-300">Dashboard</Link>
            </li>
            <li className="mb-4">
              <Link to="/companies" className="text-lg hover:text-gray-300">View Companies</Link>
            </li>
            <li className="mb-4">
              <Link to="/shipments" className="text-lg hover:text-gray-300">View Shipments</Link>
            </li>
            <li className="mb-4">
              <Link to="/subscribers" className="text-lg hover:text-gray-300">Manage Subscribers</Link>
            </li>
          </ul>
        </nav>
      </div>

      {/* Main content */}
      <div className="flex-1 p-8">
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          {/* Stats */}
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Total Companies</h3>
            <p className="text-3xl">{dummyStats.companies}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Total Shipments</h3>
            <p className="text-3xl">{dummyStats.shipments}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Pending Subscribers</h3>
            <p className="text-3xl">{dummyStats.pendingSubscribers}</p>
          </div>
        </div>

        {/* Display message */}
        {message && (
          <p
            className={`mt-4 text-center ${
              messageType === 'success' ? 'text-green-600' : 'text-red-600'
            }`}
          >
            {message}
          </p>
        )}

        {/* Manage Subscribers Section */}
        <h2 className="text-xl font-semibold mt-6 mb-4">Subscribers Waiting to List Their Companies</h2>
        
        <div className="space-y-4">
          {subscribers.length === 0 ? (
            <p>No subscribers found.</p>
          ) : (
            subscribers.map((subscriber) => (
              <div key={subscriber.email} className="flex justify-between items-center border-b pb-4">
                <div>
                  <p className="text-lg font-medium">{subscriber.merge_fields.COMPANY}</p>
                  <p className="text-sm text-gray-600">{subscriber.email}</p>
                </div>
                <button
                  onClick={() => handleRemoveSubscriber(subscriber.email)}
                  className="bg-red-600 text-white py-2 px-4 rounded-lg shadow-md hover:bg-red-700 transition"
                >
                  Remove
                </button>
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
