// AdminDashboard.js
import React, { useState, useEffect } from 'react';
import { useData } from '../context/DataContext'; // Import custom hook to access context data\
import SubscribersList from '../components/SubscribersList';
import Sidebar from '../components/Sidebar'; // Import Sidebar component

const AdminDashboard = ({onLogout}) => {
  const { companies, shipments, subscribers, setSubscribers } = useData(); // Get companies, shipments, and subscribers from context
  const [message, setMessage] = useState('');
  const [messageType, setMessageType] = useState('');

  const handleRemoveSubscriber = async (email) => {
    try {
      const response = await fetch('http://localhost:4000/api/subscribers/delete', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email }),
      });

      if (response.ok) {
        setMessage('Subscriber removed successfully');
        setMessageType('success');
        setSubscribers(subscribers.filter((subscriber) => subscriber.email !== email)); // Remove subscriber from the list
      } else {
        setMessage('Failed to remove subscriber');
        setMessageType('error');
      }
    } catch (error) {
      setMessage('An error occurred while removing the subscriber');
      setMessageType('error');
    }
  };

  const totalCompanies = companies.length;
  const totalShipments = shipments.length;
  const pendingSubscribers = subscribers.filter((subscriber) => subscriber.status === 'subscribed').length;

  return (
    <div className="flex bg-gray-100 h-full">
      {/* Main content */}
      <div className="flex-1 p-8 ml-64">
        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-6">
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Total Companies</h3>
            <p className="text-3xl">{totalCompanies}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Total Shipments</h3>
            <p className="text-3xl">{totalShipments}</p>
          </div>
          <div className="bg-white p-6 rounded-lg shadow-md">
            <h3 className="text-lg font-semibold mb-2">Pending Subscribers</h3>
            <p className="text-3xl">{pendingSubscribers}</p>
          </div>
        </div>

        {/* Notification messages */}
        {message && (
          <p className={`mt-4 text-center ${messageType === 'success' ? 'text-green-600' : 'text-red-600'}`}>
            {message}
          </p>
        )}

      </div>
    </div>
  );
};

export default AdminDashboard;
