import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import CompanyList from './components/CompanyList';
import ShipmentList from './components/ShipmentList';
import ShipmentDetails from './components/ShipmentDetails';
import Sidebar from './components/Sidebar';
import SubscribersList from './components/SubscribersList';
import Login from './screens/Login';
import Register from './screens/Register';
import AdminDashboard from './screens/AdminDashboard';
import UserDashboard from './screens/UserDashboard';
import { DataProvider } from './context/DataContext'; // Import the DataProvider
import { HiMenu } from 'react-icons/hi'; // Import hamburger icon

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [role, setRole] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false); // Sidebar state


  const handleLogin = (role, token) => {
    setIsAuthenticated(true);
    setRole(role);
    localStorage.setItem('authToken', token); // Save token in localStorage
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setRole(null);
    localStorage.removeItem('authToken');  // Clear from localStorage
    document.cookie = 'authToken=; Max-Age=0; path=/';  // Clear cookie
  };

  useEffect(() => {
    const checkAuth = async () => {
      // Check if the auth token exists in localStorage
      const storedToken = localStorage.getItem('authToken');
      console.log(storedToken);

      if (!storedToken) {
        setIsAuthenticated(false);
        return;
      }

      try {
        const response = await fetch('http://localhost:4000/api/check-auth', {
          credentials: 'include',
        });

        if (response.ok) {
          const data = await response.json();
          setIsAuthenticated(true);
          setRole(data.role); // Set the role based on response
        } else {
          setIsAuthenticated(false);
        }
      } catch (error) {
        console.error('Error checking authentication:', error);
        setIsAuthenticated(false);
      }
    };

    checkAuth();
  }, []);

  useEffect(() => {
    if (isSidebarOpen) {
      setIsSidebarOpen(false); // Close sidebar on route change
    }
  }, [location]);

  return (
    <Router>
      <DataProvider>
        <div className="flex h-screen">
        {isAuthenticated && role === 'admin' && (
            <>
              {/* Hamburger Menu Button for Small Screens */}
              <button
                className="md:hidden absolute top-4 left-4 z-40 bg-blue-600 text-white p-3 rounded-full"
                onClick={() => setIsSidebarOpen(!isSidebarOpen)} // Toggle sidebar open state
              >
                <HiMenu size={24} /> {/* Hamburger icon */}
              </button>

              {/* Sidebar */}
              <Sidebar 
                onLogout={handleLogout} 
                isSidebarOpen={isSidebarOpen} 
                onClose={() => setIsSidebarOpen(false)} 
              />
            </>
          )}

          {/* Main content */}
          <div className="flex-1">
            <Routes>
              {!isAuthenticated ? (
                <>
                  <Route path="/login" element={<Login onLogin={handleLogin} />} />
                  <Route path="/register" element={<Register />} />
                  <Route path="*" element={<Navigate to="/login" />} />
                </>
              ) : role === 'admin' ? (
                <>
                  <Route path="/" element={<AdminDashboard />} />
                  <Route path="/companies" element={<CompanyList />} />
                  <Route path="/subscribers" element={<SubscribersList />} />
                  <Route path="/shipments/:companyId" element={<ShipmentList />} />
                  <Route path="/shipment/:shipmentId" element={<ShipmentDetails />} />
                  <Route path="*" element={<Navigate to="/" />} />
                </>
              ) : (
                <>
                  <Route path="/" element={<UserDashboard onLogout={handleLogout} />} />
                  <Route path="*" element={<Navigate to="/" />} />
                </>
              )}
            </Routes>
          </div>
        </div>
      </DataProvider>
    </Router>
  );
}

export default App;
