import React from 'react';
import { HiMenu } from 'react-icons/hi';

const Sidebar = ({ onLogout, isSidebarOpen, setIsSidebarOpen }) => {
  return (
    <div className={`md:w-64 bg-gray-800 text-white ${isSidebarOpen ? 'block' : 'hidden'} md:block`}>
      {/* Sidebar content */}
      <button
        className="md:hidden absolute top-4 left-4 z-50 bg-blue-600 text-white p-3 rounded-full shadow-lg hover:bg-blue-700 transition-colors"
        onClick={() => setIsSidebarOpen(!isSidebarOpen)}
      >
        <HiMenu size={24} /> {/* Hamburger icon */}
      </button>

      <div className="p-6">
        <button onClick={onLogout} className="bg-red-500 text-white p-2 rounded mt-4">Logout</button>
        {/* Additional sidebar content goes here */}
      </div>
    </div>
  );
};

export default Sidebar;
