import React from 'react';
import { Link, useNavigate } from 'react-router-dom';

const Sidebar = () => {
  const navigate = useNavigate();

  const handleLogout = () => {
    localStorage.removeItem('authToken');
    navigate('/login');
  };

  return (
    <div className="w-64 bg-gray-800 text-white p-6 h-full fixed top-0 left-0 flex flex-col">
      <h2 className="text-xl font-bold mb-6">
        <Link to="/">Admin Dashboard</Link>
      </h2>
      <nav className="flex-grow">
        <ul>
          <li className="mb-4">
            <Link to="/companies" className="text-lg hover:text-gray-300">View Companies</Link>
          </li>
          <li className="mb-4">
            <Link to="/subscribers" className="text-lg hover:text-gray-300">View Subscribers</Link>
          </li>
        </ul>
      </nav>

      {/* Logout button at the bottom */}
      <button
        onClick={handleLogout}
        className="bg-red-500 text-white py-2 px-4 rounded-lg w-full text-center hover:bg-red-600 mt-auto"
      >
        Logout
      </button>
    </div>
  );
};

export default Sidebar;
